UPDATE `site_pages_pages` SET `no_index` = 1 WHERE `uri` = '/search-results/';


UPDATE `email_templates` SET `body` = '<p>Dear {if $listing.user_sid.value == 0}Admin{else}{$listing.user.FirstName}{/if},</p>\r\n<p>You have received a new booking request on {$GLOBALS.site_url} for the following listing of yours:</p>\r\n<p><a href="{$GLOBALS.site_url}/listing/{$listing.id}/">#{$listing.id} "{$listing}"</a></p>\r\n<p>Here are the booking details:</p>\r\n<p>Sender`s Name: {$sender_name}<br />\r\n   Sender`s Email: {$sender_email}<br />\r\n   Booking from {$period_start} to {$period_end}<br />\r\n   Comments and Instructions:<br />\r\n   {$comment}</p>\r\n\r\n{capture name=message}\r\n    Sender`s Name: {$sender_name}<br />\r\n    Sender`s Email: {$sender_email}<br />\r\n    Booking from {$period_start} to {$period_end}<br />\r\n    Comments and Instructions:<br />\r\n    {$comment}\r\n{/capture}\r\n\r\n<p>\r\n    <br />\r\n    To perform the reservation for this property, please go to our Manage Calendar area by clicking the link below:<br />\r\n    <a href="{$GLOBALS.site_url}/manage-calendar/?action=preview_book&listing_sid={$listing.id}&field_sid={$field_sid}&from={$period_start}&to={$period_end}&comment={$smarty.capture.message|escape:"url"}">Manage Calendar</a><br />\r\n    <br />\r\n     {$GLOBALS.site_url} Administrator\r\n</p>\r\n' WHERE `id` = 'book_request';
