INSERT INTO `site_pages_pages` (`id`, `uri`, `pass_parameters_via_uri`, `module`, `function`, `template`, `title`, `application_id`, `parameters`, `keywords`, `description`) VALUES
('cookie_policy', '/cookie-policy/', 0, 'cookie_notice', 'display_notice', '', 'Cookie Policy', 'FrontEnd', 'a:0:{}', '', '');
INSERT INTO `static_content_pages` (`id`, `name`, `content`, `title`) VALUES
('CookiePolicy_en', 'CookiePolicy_en', '<h1>\r\n	Our Cookie Policy</h1>\r\n<h2>\r\n	What Are Cookies</h2>\r\n<p>\r\n	Cookies can be defined as text files that browsers create on your computer to uniquely identify you or store certain information about you. Cookies are stored as text files on your computer. Various websites use cookies for a number of purposes. We also use cookies to store settings and options you chose and to positively identify you when you come back.</p>\r\n<p>\r\n	For best user experience, it is recommended that you allow cookies for our website in order to get the most out of it. If you prefer to keep the cookies turned off for whatever reason, you will still be able to use our website. However, certain essential functions and capabilities such as adding listings, may not be available in full. This is why we do recommend that you turn on the cookies.</p>\r\n<p>\r\n	Cookies don&#39;t harm your computer. We don&#39;t store personally identifiable information such as credit card details in cookies we create.</p>\r\n<p>\r\n	You can learn more about cookies in the <a href="http://en.wikipedia.org/wiki/HTTP_cookie" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">Wikipedia article</a>.</p>\r\n<h2>\r\n	The Cookies We Create</h2>\r\n<ul>\r\n	<li>\r\n		FrontEnd_voted_questions - contains the list of answered questions in polls</li>\r\n	<li>\r\n		FrontEnd_SAVED_LISTINGS - contains visitor&#39;s saved listings</li>\r\n	<li>\r\n		SAVED_SEARCHES - contains visitor&#39;s saved searches</li>\r\n	<li>\r\n		FrontEnd_session_key - used for automatic user login, who earlier selected the option &quot;Keep me signed in&quot;</li>\r\n	<li>\r\n		MobileFrontEnd_session_key - used for automatic user login to the mobile version of the website, who earlier selected the option &quot;Keep me signed in&quot;</li>\r\n</ul>\r\n<h2>\r\n	Third-Party Cookies</h2>\r\n<p>\r\n	Like other online companies, we use third-party cookies to provide you with the features related to external service &mdash; like logging to our site with Facebook or Twitter accounts, helping you explore the website better and easier.</p>\r\n<p>\r\n	Here are the links where you can learn more about cookies used by other services:</p>\r\n<ul>\r\n	<li>\r\n		<a href="http://developers.facebook.com/docs/reference/fql/cookies/" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">Facebook cookies</a></li>\r\n	<li>\r\n		<a href="https://twitter.com/privacy" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">Twitter privacy</a></li>\r\n	<li>\r\n		<a href="http://www.addthis.com/privacy/privacy-policy" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">AddThis privacy policy</a></li>\r\n	<li>\r\n		<a href="http://www.youtube.com/t/privacy" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">YouTube privacy</a></li>\r\n</ul>\r\n', NULL),
('CookiePolicy_ru', 'CookiePolicy_ru', '<h1>\r\n	Политика использования куки на нашем вебсайте</h1>\r\n<h2>\r\n	Что такое куки</h2>\r\n<p>\r\n	Куки (англ. cookie) &mdash; это маленькие текстовые файлы, хранящиеся на вашем компьютере, которые создаются после посещения вами определенных веб-страниц. На нашем сайте мы используем куки, чтобы отслеживать выбранные вами на сайте опции, и помнить вас, когда вы возвращаетесь.</p>\r\n<p>\r\n	Чтобы получить лучший опыт по использованию нашего сайта, куки должны быть включены. Если вы не хотите включать куки, вы все равно сможете пользоваться сайтом, но вы можете не иметь возможность использовать важнейшие функции, которые мы предлагаем, такие как добавление объявления, поэтому мы рекомендуем вам держать куки включенными.</p>\r\n<p>\r\n	Куки не вредят вашему компьютеру. Мы не храним личную идентифицирующую информацию, такую как номер кредитной карты и другие ее подробности, в тех куки, которые мы создаем.</p>\r\n<p>\r\n	Вы можете узнать больше о куки в <a href="http://ru.wikipedia.org/wiki/HTTP_cookie" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">статье Википедии</a>.</p>\r\n<h2>\r\n	Куки, которые создаем мы</h2>\r\n<ul>\r\n	<li>\r\n		FrontEnd_voted_questions - содержит список отвеченных вопросов из голосования</li>\r\n	<li>\r\n		FrontEnd_SAVED_LISTINGS - содержит сохранненые объявления для посетителя (неавторизованного пользователя)</li>\r\n	<li>\r\n		SAVED_SEARCHES - содержит сохраненные поиски для посетителя (неавторизованного пользователя)</li>\r\n	<li>\r\n		FrontEnd_session_key - используется для автоматического логина пользователя, который ранее выбрал опцию &quot;Запомнить меня&quot;</li>\r\n	<li>\r\n		MobileFrontEnd_session_key - используется для автоматического логина пользователя, который ранее выбрал опцию &quot;Запомнить меня&quot;</li>\r\n</ul>\r\n<h2>\r\n	Куки третьих сторон</h2>\r\n<p>\r\n	Как и другие онлайн-компании, мы используем куки третьих сторон, чтобы дать вам возможность воспользоваться функционалом, относящимся ко внешним сервисам; например, возможность логина на наш вебсайт с учетной записью Фейсбука или Твиттера, облегчая вам использование сайта.</p>\r\n<p>\r\n	Здесь перечислены ссылки страниц, где вы можете узнать больше о куки, используемых третьесторонними сервисами:</p>\r\n<ul>\r\n	<li>\r\n		<a href="http://developers.facebook.com/docs/reference/fql/cookies/" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">Куки Фейсбука</a></li>\r\n	<li>\r\n		<a href="https://twitter.com/privacy" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">Отношение к личной информации у Твиттера</a></li>\r\n	<li>\r\n		<a href="http://www.addthis.com/privacy/privacy-policy" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">Политика личной информации в AddThis</a></li>\r\n	<li>\r\n		<a href="http://www.youtube.com/t/privacy" onclick="javascript:window.open(this.href, ''_blank''); return false;" rel="nofollow">Отношение к личной информации у сервиса YouTube</a></li>\r\n</ul>\r\n', NULL);
